mapper.insert:
INSERT INTO USER (
                  USER_NAME
                  , USER_ID
                  , USER_PASSWORD
                  , PHONE_NUMBER
                  , USER_ADRESS
                  , REGISTER_DATETIME
                  , USER_YN
) VALUES (
          #{userName}
         , #{userId}
         , #{userPassword}
         , #{phoneNumber}
         , #{userAddress}
         , NOW()
         , 'y'
                 )

mapper.select:
SELECT COUNT(U.USER_ID)
FROM USER U
WHERE USER_ID = #{userId}
